```typescript:App.tsx
import * as React from 'react'
import { useEffect } from 'react'
import { NavigationContainer } from '@react-navigation/native'
import { RootStackNavigator, StackParamList } from './src/pages/root/root.navigator'
import { StripeProvider } from '@stripe/stripe-react-native'
import { ToastProvider, useToast } from 'react-native-toast-notifications'
import { initializeToast } from './src/utils/toast'
import { authApi } from './src/api/core/api.auth'
import { STRIPE_PUB_KEY } from '@env'

const deeplinkConfig: LinkingOptions<StackParamList> = {
  // ... existing deeplink config ...
}

const App = () => {
  const toast = useToast()

  useEffect(() => {
    if (toast) {
      initializeToast(toast)
    }
  }, [toast])

  // Add new useEffect for auth check
  useEffect(() => {
    const checkAuthStatus = async () => {
      try {
        const loggedIn = await authApi.checkIfLoggedIn()
        console.log('loggedIn: ', loggedIn)

        if (loggedIn) {
          // We need to use the navigation ref since we're outside of a screen component
          navigationRef.current?.navigate('CaregiverHome')
        }
      } catch (error) {
        console.error('Auth check failed:', error)
      }
    }

    checkAuthStatus()
  }, [])

  return (
    <ToastProvider
      placement="top"
      duration={4000}
      animationType="slide-in"
      offsetTop={50}
      swipeEnabled={true}
    >
      <StripeProvider publishableKey={STRIPE_PUB_KEY}>
        <NavigationContainer linking={deeplinkConfig}>
          <RootStackNavigator />
        </NavigationContainer>
      </StripeProvider>
    </ToastProvider>
  )
}

export default App
```

